import { useState } from 'react';
import axios from 'axios';
const useForecast = () => {
    const [isError, setError] = useState(false);
    const [isLoading, setLoading] = useState(false);
    const forecast={}

    const getData = async location => {
        const  response = await axios(`http://api.openweathermap.org/geo/1.0/direct?q=${location}&appid=8daa67bc5aa6f4004f105953f01aadfd`);
        if (!response || response.length === 0) {
            setError('There is no such location');
            setLoading(false);
            return;
        }
        return response.data[0];
    };

    const getForecastData = async data => {
        const lat=data.lat;
        const lon=data.lon;
        const  response  = await axios(`http://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=8daa67bc5aa6f4004f105953f01aadfd`);
        if (!response || response.length === 0) {
            setError('Something went wrong');
            setLoading(false);
            return;
        }
        return response;
    };
    const gatherForecastData = response => {
        forecast.push({temp:response.data.main.temp});
        forecast.push({temp_min:response.data.main.temp_min})
        forecast.push({temp_max:response.data.main.temp_max})
        forecast.push({feels_like:response.data.main.feels_like});
        forecast.push({sunrise:response.data.sys.sunrise});
        forecast.push({sunset:response.data.sys.sunset});
        setLoading(false);
    };

    const submitRequest = async location => {
        setLoading(true);
        setError(false);
        const response = await getData(location);
        if(!response) return;
        const data = await getForecastData(response);
        if (!data) return;
        gatherForecastData(data);
        console.log(forecast);
    };

    return {
        isError,
        isLoading,
        forecast,
        submitRequest,
    };
};


export default useForecast;
