import useForecast from '../hooks/useForecast';
import Context from "../context";
import React, { useEffect, useState } from 'react';
import {useNavigate} from 'react-router-dom';
import Weather from './Weather';
function Dashboard()
{
    const [city,setCity]=useState('');
    const {forecast, submitRequest } = useForecast();
    let navigate=useNavigate();
    function clickHandler2()
    {
        localStorage.clear()
        navigate('/')
    }
    function clickHandler(e)
    {
      e.preventDefault();
      console.log(city)
      submitRequest(city)
      console.log(forecast);
    }
    return(
    <div >
      <div className=" grid container ">
         <button onClick={clickHandler2}  className="fluid ui button blue">Logout</button>
      </div>
      <div className="grid container ">
          <h2>Weather Forecast</h2>
      </div>
      <div className="grid container ">
          <h3>Get All Your Essential Weather Data For A Specific Location</h3>
      </div>
      <div className="container">
        <form>
        <div className="ui divider"></div>
        <div className="ui form">
          <div className="field">
            <input type="text" placeholder='city name' onChange={(e)=>{setCity(e.target.value)}} required/>
          </div>
          <div className="field">
          <button onClick={clickHandler} className="fluid ui button blue">Get Details</button>
          </div>
          <div className="grid container ">
          <h3>Next 7 Days Temperature for {city}</h3>
      </div>
        </div>
      </form>
     if({forecast}!==null)
     {
       <Context.Provider value={{forecast}}> 
       <Weather/>
       </Context.Provider> 
     }
    </div>
    </div>
    )
}
export default Dashboard;
