import axios from 'axios';
const useForecast = () => {
    const submitRequest = async location => {
       const response=await axios(`http://api.openweathermap.org/geo/1.0/direct?q=${location}&appid=8daa67bc5aa6f4004f105953f01aadfd`)
       const name=response.data[0].name;
       const lat=response.data[0].lat;
       const lon=response.data[0].lon;
       console.log(response.data);
       submitRequest2(lat,lon);
    };
    const submitRequest2=async (lat,lon)=>{
        console.log(lat,lon);
        const response2=await axios(`http://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=8daa67bc5aa6f4004f105953f01aadfd`)
        console.log(response2.data.main.temp,response2.data.main.temp_max,response2.data.main.temp_min,response2.data.main.temp);
        console.log(response2.data.sys.sunrise,response2.data.sys.sunset);
    }

    return {
        submitRequest
    };
};

export default useForecast;
